import React from "react";
import styled from "styled-components";

const Tips = () => {

    return (
        <Wrapper>
            <Content>
                <p>Select Tip %</p>
                <TipsGrid>
                    <Tip>5%</Tip>
                    <Tip>10%</Tip>
                    <Tip>15%</Tip>
                    <Tip>25%</Tip>
                    <Tip>50%</Tip>
                    <Tip>2%</Tip>
                </TipsGrid>
            </Content>
        </Wrapper>
    )
}

export default Tips;

const Wrapper = styled.div``;

const Content = styled.div`
    padding: 0 20px;

    p {
        color: var(--medGrey);
        font-weight: 600;
    }
`;

const TipsGrid = styled.div`
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 10px;
`;

const Tip = styled.button`
    border: none;
    background-color: hsl(183, 100%, 15%);
    height: 50px;
    color: #fff;
    font-size: 1.5rem;
    border-radius: 10px;
    cursor: pointer;
    font-weight: 600;

    :hover {
        background-color: hsl(172, 67%, 45%);
    }
`;
