import React from "react";
import styled from "styled-components";

const Results = () => {

    return (
        <Wrapper>
            <Content>
                <ResultsBox>
                    <Label>
                    <h3>Tip Amount</h3>
                    <p>/ person</p>
                    </Label>
                    <h3>$0.00</h3>
                </ResultsBox>
                <ResultsBox>
                    BOX 2
                </ResultsBox>
                <Reset>RESET</Reset>
            </Content>
        </Wrapper>
    )
}

export default Results;

const Wrapper = styled.div`
    background-color: #00494D;
    height: 100%;
    border-radius: 25px;
`;

const Content = styled.div`
    display: flex;
    flex-direction: column;
    gap: 30px;
    padding: 30px;
    position: relative;
    height: 100%;
`;

const ResultsBox = styled.div`
    display: flex;
    justify-content: space-between;
`;

